public class displayStuff {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		displayName();
		displayFact();
		displayBirthday();


	}
	
	/**
	 * displayName will display my name to the user 
	 * 
	 * @param there are no parameters
	 * @return Nothing will be returned
	 */
	public static void displayName(){
		
		System.out.println("My name is Amanda");
	}
	
	//---------------------------------------------------------------------
	
	/**
	 * displayFact will display a fact about me to the user
	 * 
	 * 
	 * @param there are no parameters
	 * @return Nothing will be returned
	 */
	
	public static void displayFact(){
		
		System.out.println("An interesting fact about me is I like popcorn ");
	}
	
	//----------------------------------------------------------------------

	
	/**
	 * displayBirthday will display my Birthday to the 
	 * user
	 * 
	 * @param there are no parameters
	 * @return Nothing will be returned
	 */
	
	public static void displayBirthday(){
		
		System.out.println("My birthday is March 27");
	}

	

}
	


